a {
	cursor: pointer;
}
.nav-bar {
	position: relative;
	display: flex;
	align-items: center;
	padding: 1rem;
	background-color: rgba(0, 0, 0, 0);
	color: #fff;
	z-index: 999;
}

.logo img {
	height: 40px;
	margin-left: 60px;
}

.nav-links {
	margin-left: 60px;
	list-style: none;
	display: flex;
	gap: 6rem;
}

.nav-links li {
	font-size: 2rem;
}

.search-user {
	display: flex;
	align-items: center;
	margin-left: auto;
	margin-right: 100px;
}

.search-bar {
	position: relative;
	margin-right: 8rem;
}

.search-bar input {
	padding: 1.5rem;
	border: none;
	border-radius: 30px;
	outline: groove;
	outline-color: #fff;
	outline-width: thin;
	width: 50vh;
	background: rgba(255, 255, 255, 0.26);
	color: white;
	font-size: 1.4rem;
}

.search-icon {
	position: absolute;
	right: 10px;
	top: 50%;
	transform: translateY(-50%);
	cursor: pointer;
}

::placeholder {
	color: #fff;
}

.user-icon {
	display: flex;
	align-items: center;
	cursor: pointer;
}

.user-icon svg {
	margin-right: 0.5rem;
}

.dropdown {
	display: flex;
	flex-direction: column;
	position: absolute;
	background-color: #000;
	padding: 1rem;
	margin-top: 4rem;
	margin-right: 8rem;
	border-radius: 5px;
	top: 2rem;
	right: 0;
	width: 6%;
}

.dropdown button {
	background: none;
	color: #fff;
	border: none;
	cursor: pointer;
	padding: 0.5rem;
	text-align: left;
	font-size: 1.5rem;
	letter-spacing: 0.1rem;
	line-height: 2.5rem;
}

.dropdown button:hover {
	background-color: rgba(255, 255, 255, 0.1);
}

.login-signup-links {
	display: flex;
	align-items: center;
	font-size: 2rem;
	font-weight: 500;
}

.separator {
	margin: 0 1.5rem;
}

.popup {
	position: fixed;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	background-color: white;
	padding: 20px;
	border-radius: 5px;
	box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
	z-index: 999;
	width: 25%;
}

.overlay {
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background-color: rgba(0, 0, 0, 0.8);
	z-index: 999;
}

.popup_title {
	font-size: 2rem;
	font-weight: 800;
	margin-bottom: 1rem;
	color: #000;
}

.popup_form {
	display: flex;
	flex-direction: column;
	align-items: center;
	gap: 2rem;
}

.popup_form input {
	padding: 1.5rem;
	border-radius: 5px;
	width: 100%;
	font-size: 1.8rem;
}

.popup_form select {
	padding: 1.5rem;
	border-radius: 5px;
	width: 100%;
	font-size: 1.8rem;
	background-color: #fff;
	color: #000;
}

.popup_form button {
	padding: 1rem;
	border-radius: 5px;
	width: 100%;
	background-color: #2196f3;
	color: #fff;
	border: #167dd1 solid 2px;
}

.popup_form button:hover {
	background-color: #167dd1;
}

.popup_form label {
	font-size: 1.8rem;
	font-weight: 500;
	color: #000;
}

.form-group {
	margin-bottom: 10px;
	width: 100%;
}

.form-group label {
	display: block;
	margin-bottom: 5px;
}

.profile-popup {
	background: #292929;
}

.centered {
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	text-align: center;
}

.profile_data button {
	padding: 1rem;
	border-radius: 5px;
	width: 100%;
	background-color: #fb3131;
	color: #fff;
	border: #a50f0f solid 2px;
}

.profile_data button:hover {
	background-color: #eb7c72;
}

