import { ref, watch } from "vue";
import { useSearchStore } from "~/store/search";
import { useAuthStore } from "~/store/auth";
import { languages } from "~/mixins/Languages";

const _sfc_main = {
	data() {
		return {
			showDropdown: false,
			showSearch: this.$route.name === "search" ? true : false,
			query: this.$route.query.q ? this.$route.query.q : "",
			fromPage: useSearchStore().fromPage,
			isLoggedIn: false,
			showProfile: false,
			showLogin: false,
			showSignup: false,
			languages: [...languages],
			user: null,
		};
	},

	watch: {
		$route(to, from) {
			this.query = to.query.q || "";
		},
	},

	mounted() {
		this.$refs.input.focus();
	},

	setup() {
		const authStore = useAuthStore();
		const userId = ref("");
		const username = ref("");
		const email = ref("");
		const lang = ref("");
		const isLoggedIn = ref(false);
		const user = ref("");

		if (typeof localStorage !== "undefined") {
			// Initial sync from localStorage on page load
			if (localStorage.getItem("isLoggedIn") === "true") {
				isLoggedIn.value = true;
				user.value = JSON.parse(localStorage.getItem("user"));
			}

			// Watch for changes in authStore
			watch(
				() => authStore.isLoggedIn,
				(newValue) => {
					isLoggedIn.value = newValue;
					localStorage.setItem("isLoggedIn", newValue); // Update localStorage
				},

				() => authStore.user,
				(newValue) => {
					user.value = newValue;
				}
			);
		}

		if (typeof window !== "undefined") {
			// Watch for localStorage changes (e.g., logged in on another tab)
			window.addEventListener("storage", (event) => {
				if (event.key === "isLoggedIn") {
					isLoggedIn.value = event.newValue === "true";
				}
			});
		}

		const signup = async () => {
			if (!username.value || !email.value) {
				// Handle missing fields with user-friendly message
				console.error("Username and email are required");
				return;
			}
			const userData = {
				userId: userId.value,
				username: username.value,
				email: email.value,
				lang: lang.value,
			};

			try {
				await authStore.register(userData);
				closePopup();
			} catch (error) {
				console.error("Registration failed:", error);
			}
		};

		const login = async () => {
			if (!username.value) {
				// Handle missing fields with user-friendly message
				console.error("Username is required");
				return;
			}

			try {
				await authStore.login(username.value);
				this.closePopup();
			} catch (error) {
				console.error("Login failed:", error);
			}
		};

		const deleteAccount = async () => {
			try {
				await authStore.delete(user.value.user._id);
				closePopup();
			} catch (error) {
				console.error("Account deletion failed:", error);
			}
		};

		return {
			username,
			email,
			lang,
			isLoggedIn,
			user,
			signup,
			login,
			deleteAccount,
		};
	},

	methods: {
		toggleDropdown() {
			this.showDropdown = !this.showDropdown;
		},

		handleDropdownItem(item) {
			const authStore = useAuthStore();

			if (item === "Profile") {
				this.showProfilePopup();
			} else {
				authStore.logout();
				this.$router.push("/");
			}
		},

		handleFavorites() {
			this.$router.push({ name: "favorites" });
		},

		toggleSearchInput() {
			this.showSearch = !this.showSearch;
		},

		toggleSearchState() {
			// Toggle search state
			useSearchStore().toggleSearch();
		},

		goToRoute() {
			if (this.query) {
				if (this.$route.name === "search") {
					// If already on search page, update query parameter
					this.$router.replace({ query: { q: this.query } });
				} else {
					// Navigate to search page with query parameter
					this.$router.push({ name: "search", query: { q: this.query } });
				}
			} else {
				// If query is empty, go back to previous page
				this.$router.push({ path: this.fromPage });
			}
		},

		goBack() {
			// Reset query and navigate back
			this.query = "";
			this.$router.push({ path: this.fromPage });
		},

		unFocus(e) {
			const searchStore = useSearchStore();
			if (this.$route.name !== "search") {
				const target = e.relatedTarget;
				if (!target || !target.classList.contains("search-toggle")) {
					this.query = "";
					searchStore.closeSearch();
				}
			}
		},

		showProfilePopup() {
			this.showProfile = true;
			this.showLogin = false;
			this.showSignup = false;
		},

		showLoginPopup() {
			this.showLogin = true;
			this.showSignup = false;
		},

		showSignupPopup() {
			this.showSignup = true;
			this.showLogin = false;
		},

		closePopup() {
			this.showProfile = false;
			this.showLogin = false;
			this.showSignup = false;
		},
	},
};

