export const left = "_left_uljt1_7";
export const right = "_right_uljt1_25";
export const poster = "_poster_uljt1_30";
export const overview = "_overview_uljt1_51";
export const title = "_title_uljt1_63";
export const stats = "_stats_uljt1_75";
export const label = "_label_uljt1_110";
export const value = "_value_uljt1_122";
export const external = "_external_uljt1_126";
export default {
	info: info,
	left: left,
	right: right,
	poster: poster,
	overview: overview,
	title: title,
	stats: stats,
	label: label,
	value: value,
	external: external
};
