@import "~/assets/css/utilities/_variables.scss";

.stats {
	margin-bottom: 3rem;
	font-size: 1.5rem;
	color: $text-color;

	@media (min-width: $breakpoint-large) {
		font-size: 1.6rem;
	}

	ul {
		@media (min-width: $breakpoint-medium) {
			display: flex;
			flex-wrap: wrap;
		}
	}

	li {
		display: flex;
		align-items: center;
		flex: 1; /* Equal width for all items */
		padding: 0.2rem 0;

		@media (min-width: $breakpoint-medium) {
			width: calc(33.33% - 1rem); /* Adjust as needed */
			margin-right: 1rem; /* Space between items */
		}

		@media (min-width: $breakpoint-xlarge) {
			width: 100%;
			margin-right: 0;
		}
	}

	a {
		color: $primary-color;
		text-decoration: underline;
	}
}

.label {
	flex: 1;
	max-width: 90px;
	margin-right: 1.5rem;
	color: #fff;

	@media (min-width: $breakpoint-xsmall) {
		max-width: 110px;
	}
}

.value {
	flex: 2;
}

.external {
	ul {
		display: flex;
		margin-left: -0.5rem;
	}

	a {
		display: flex;
		align-items: center;
		justify-content: center;
		width: 4.4rem;
		height: 4.4rem;

		svg {
			transition: all 0.3s ease-in-out;
		}

		&:hover,
		&:focus {
			svg {
				fill: $primary-color;
			}
		}
	}
}

